import React from 'react'
import BannerImage from '../assets/background.jpeg'
import '../styles/Home.css'
import '../styles/Add_guides.css'


function Filter_device() {
  const data =[{"model":"P9 Lite"},{"model":"P10 Plus"}];
    return (
        <div className="home" style={{backgroundImage: `url(${BannerImage})`}}>            
            <form>
      <label>
        Brand:
        <select id="optionSelected"
          onClick={models}
          name="brand"
          required>
          <option key="default"></option>
          <option value="Samsung">Samsung</option>
          <option value="Huawei">Huawei</option>
          <option value="Apple">Apple</option>
        </select>
      </label>

      <label>
        Model:

        <option key="default"></option>
      </label>


      <button>Search</button>
  </form>
    </div>
    );
}

function models(){
    var x = document.getElementById("optionSelected").value;

    if (x === 'Samsung') {
      const data =[{"model":"Galaxy Tab S8"},{"model":"Galaxy S8"}];
    }
    if (x === 'Huawei') {
      const data =[{"model":"P9 Lite"},{"model":"P10 Plus"}];

    return (
      <div>
      <select
          name="models"
          required>
          <option key=""></option>
          {data.map(d => (
            <option key={d.model}>{d.model}</option>
          ))}
        </select>
      </div>
    );
}
}

export default Filter_device
