import '../styles/Home.css'
import '../styles/Add_guides.css'
import React from "react";
import '../styles/popup.css';
import Popup from 'reactjs-popup'
import PhoneImage from '../styles/screen_s8.jpeg'
import step1 from '../styles/screen_s8_step1.jpeg'
import step2 from '../styles/screen_s8_step2.jpeg'
import step3 from '../styles/screen_s8_step3.jpeg'
import step4 from '../styles/screen_s8_step4.jpeg'
import step5 from '../styles/screen_s8_step5.jpeg'
import author from '../styles/screen_s8_author.jpeg'
import '../styles/Specific_Guides.css'
import { Link } from 'react-router-dom'



class Test extends React.Component {

    constructor() {

        super();

        this.showAll = this.showAll.bind(this);
		this.showComments1 = this.showComments1.bind(this);

        this.state = {

            showComponent: false,
        };

    }


    showAll(e) {
        this.setState({
            showIphone6: e.target.checked,
        });
    }
	showComments1(e) {
        this.setState({
			showIphone8: e.target.checked,
        });
    }

    render() {

           return (


                <div className="home2">
            <div className="headerContainer_Sp_Guide">
                <p>Store for Parts</p>
            </div>



            <div className="text" text-align="center">   
                <p>Here you can find all Tools needed for your repairs</p>
            </div> 
            <div className="big_container_no_border">
            <div className="step_text">
                <p><input className="checkbox" type="checkbox" name="enable_comment" onClick={this.showAll} value="enable_comment"/>All</p>
				<p><input className="checkbox" type="checkbox" name="enable_comment" onClick={this.showComments1} value="enable_comment"/>Pryin</p>
                {this.state.showIphone6 ? <div  className="comments_preview_sample"><p>- Slide the opening pick down the side of the phone, separating the adhesive.</p>
                    <p>Note: Go slowly so that the tool doesn't slip out of the seam. If cutting becomes difficult, reheat and reapply the iOpener.</p></div> : null}
				{this.state.showIphone8 ? <div  className="comments_preview_sample">Hello1</div> : null}



                </div>
                <div className="step_text">



                </div>
            </div>









            </div>










        )
    }
}

export default Test
