    { id: 'Galaxy S8', text: 'Galaxy S8' },
    { id: 'Galaxy S9', text: 'Galaxy S9' },
    { id: 'Galaxy S10', text: 'Galaxy S10' }
  ],
  "Apple": [
    { id: 'Iphone 7', text: 'Iphone 7' },
    { id: 'Iphone X', text: 'Iphone X' },
    { id: 'Iphone 12', text: 'Iphone 12' }
  ]
}

