import React, {useState} from 'react'
import Logo from "../assets/Logo1.png"
import {Link, NavLink} from 'react-router-dom'
import ReorderIcon from '@mui/icons-material/Reorder';
import '../styles/Navbar.css'

function Navbar() {

    const [openLinks, setOpenLinks] = useState(false);
    const toggleNavbar = () => { 
        setOpenLinks(!openLinks)
    }

    const activeTab = (history, path) => {
        if (history.location.pathname === path) {
          return { color: "red" };
        }
      };

    const Hello =  ({ history }) => {return (
        <div className="navbar">
            <div className="leftSide" id={openLinks ? "open" : "close"}>
                <NavLink to="/">
                    <img src={Logo} />
                </NavLink>
                <div className="hiddenLinks">
                    <NavLink activeStyle={{color: "red"}}  to="/add_guides" > Add Guides </NavLink>
                <NavLink to="/about_us"> About </NavLink>
                <NavLink to="/login"> Login </NavLink>

                </div>
            </div>
            <div className="rightSide">
                <NavLink to="/add_guides"  exact> Add Guides </NavLink>
                <NavLink to="/about_us" activeStyle={{color: "red"}}> About </NavLink>
                <NavLink to="/login" activeStyle={{color: "red"}}> Login </NavLink>
                <button onClick={toggleNavbar}>
                    <ReorderIcon />
                </button>
            </div>
        </div>
    )
    }
}

export default Navbar
