import BannerImage from '../assets/background.jpeg'
import '../styles/Home.css'
import '../styles/Add_guides.css'
import React,{handleSubmission,setSelectedFile,setIsSelected,selectedFile,isSelected} from "react";
import Popup from 'reactjs-popup';
import '../styles/popup.css';
import {Link} from 'react-router-dom'
import ReactDOM from 'react-dom'



const { Component } = React;
const { render } = ReactDOM;

class test extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			showComponent: false
		}
		this.showComments = this.showComments.bind(this);
	}

	showComments() {
		this.setState({
			showComponent: !this.state.showComponent
		});
	}

	render() {
		return (
			<div>
				<div className="add_checkbox">
					Enable Comments <br/>
					<input type="checkbox" onClick={this.showComments} />
				</div>
				{this.state.showComponent ? <div className="comments_preview_sample">comments</div> : null}
			</div>
		);
	}
}



export default test
